from '../util/index'

export function Observer(value) {
  this.value = value
  this.dep = new Dep()
  if(Array.isArray(value)){
    //new
    var augment = hasProto
        ? protoAugment
        : copyAugment
      augment(value, arrayMethods, arrayKeys)
    this.observeArray(value)
  }else{
    this.walk(value)
  }
  def(value, '__ob__', this)
}

